import './App.css';
import {questionMap} from './questionMap.js';
import { useState, Fragment, useRef, useEffect } from 'react';

function App(props) {
  const inputRef = useRef(null);
  const [inputs, setInputs] = useState({});

  const [inputWidth, setInputWidth] = useState("6ch");
  useEffect(() => {
    console.log("in useEffect");
    console.log(inputs);
    if (inputs[0] && inputs[1] && inputs[2]) {
      setInputWidth(inputs[0].length);
    }
  }, [inputs])

  const { questionNum } = props;
  const questionList = questionMap[questionNum];


  const inputOnChange = (event, i) => {
    console.log("what's", i);
    let newInputs = {...inputs};
    newInputs[i] = event.target.value;
    setInputs(newInputs);
  }

  const inputStyle = {
    width: inputWidth
  }

  const questionListDOM = questionList.map((line, i) =>
    <Fragment>
      {line} 
      <input
        ref={inputRef}
        style={inputStyle}
        value={inputs[i]}
        onChange={(event, i) => inputOnChange(event, i)}
        className="line-input"
        placeholder="______"
      />
      .<br/>
    </Fragment> 
  )

  const copyLines = () => {
    console.log("clicked on copy");
  }

  return (
    <div className="App">
      {questionNum} is the questionNum.

      <div class="lines">
        <span class="fillin">Fill in the blank.</span>
        {questionListDOM}
      </div>
      <div class="action">
        <span class="button" onClick={copyLines}>copy my lines</span>
      </div>
      <div class="footer">
        &copy; 2021 Kumiko Kashii
      </div>
    </div>
  );
}

export default App;
