import './App.css';
import {questionMap} from './questionMap.js';
import { Fragment } from 'react';

function App(props) {
  const { questionNum } = props;
  const questionList = questionMap[questionNum];
  const questionListDOM = questionList.map((line, i) =>
    <Fragment>
      line + <input classname="line-input" placeholder="______" size={6} /><br/>
    </Fragment> 
  )

  return (
    <div className="App">
      {questionNum} is the questionNum.
      <br/>
      {questionList} is the questionNum.

      <div class="lines">
        <span class="fillin">Fill in the blank.</span>
        {questionListDOM}
        {/* Line 1 line 1 line 1 line 1 line 1 <input class="line-input" placeholder="___" size={3} />.<br/>
        Line 2 line 2 line 2 line 2 line 2 <input class="line-input" placeholder="______" size={6} />.<br/>
        Line 3 line 3 line 3 line 3 line 3 <input class="line-input" placeholder="___" size={3} />.<br/> */}
      </div>
      <div class="action">
        <span class="button">copy my lines</span>
      </div>
      <div class="footer">
        &copy; 2021 Kumiko Kashii
      </div>
    </div>
  );
}

export default App;
